    this.filterChange$.pipe(takeUntil(this.destroy$), debounceTime(300), distinctUntilChanged((prev, curr) => JSON.stringify(prev) === JSON.stringify(curr)), switchMap(params => {
      if (!params) return [];
      console.log('ðŸš€ Making API call with params:', params); // Debug log
      this.isLoading = true;
      this.paginationService.setLoading(true);
      this.cdr.detectChanges();
      return this.listingService.getListings(params);
    })).subscribe({
      next: response => {
        console.log('âœ… API response received:', response); // Debug log
        if (this.paginationService.currentPage === 1) {
          this.listings = response.items || [];
        } else {
          this.listings = [...this.listings, ...(response.items || [])];
        }
        this.paginationService.setTotalCount(response.totalCount || 0);
        this.isLoading = false;
        this.paginationService.setLoading(false);
        this.cdr.detectChanges();
      },
      error: error => {
        console.error('âŒ Error loading listings:', error);
        this.handleLoadingError(error);
      }
    });
  }
  handleLoadingError(error) {
    this.isLoading = false;
    this.paginationService.setLoading(false);
    this.cdr.detectChanges();
  }
  // âœ… FIXED: Filter Management
  selectQuality(quality) {
    this.selectedQuality = this.selectedQuality === quality ? '' : quality;
    this.resetPaginationAndFilter();
