  onSortChange() {
    console.log('ðŸ“Š Sort changed to:', this.sortBy); // Debug log
    this.resetPaginationAndFilter();
    this.updateQueryParams();
  }
  clearQualityFilter() {
    this.selectedQuality = '';
    this.resetPaginationAndFilter();
  }
  clearAllFilters() {
    this.selectedQuality = '';
    this.priceRange = {
      min: 0,
      max: 10000
    };
    this.sortBy = 'newest';
    this.showFavoritesOnly = false;
    this.resetPaginationAndFilter();
    this.updateQueryParams();
