: flex;\n  flex-direction: column;\n  align-items: center;\n  padding: 30px 0;\n  margin-top: 20px;\n}\n.infinite-scroll-loader[_ngcontent-%COMP%]   .loading-spinner[_ngcontent-%COMP%] {\n  width: 40px;\n  height: 40px;\n  border: 3px solid rgba(168, 57, 57, 0.1);\n  border-top: 3px solid #a83939;\n  border-radius: 50%;\n  animation: _ngcontent-%COMP%_spin 1s linear infinite;\n  margin-bottom: 15px;\n}\n.infinite-scroll-loader[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  color: #666;\n  font-size: 15px;\n  font-weight: 500;\n}\n\n.end-of-results[_ngcontent-%COMP%] {\n  text-align: center;\n  padding: 25px;\n  margin: 30px 0;\n  color: #666;\n  background-color: #f9f9f9;\n  border-radius: 8px;\n  border: 1px dashed #ddd;\n}\n.end-of-results[_ngcontent-%COMP%]   p[_ngcontent-%COMP%] {\n  margin: 0;\n  font-weight: 500;\n}\n\n.end-of-results[_ngcontent-%COMP%] {\n  text-align: center;\n  padding: 20px 0;\n  margin-top: 20px;\n  color: #666;\n  background-color: #f9f9f9;\n  border-radius: 4px;\n}\n\n@keyframes _ngcontent-%COMP%_spin {\n  0% {\n    transform: rotate(0deg);\n  }\n  100% {\n    transform: rotate(360deg);\n  }\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
