<div class="container contact-container">
  <div class="contact-content">
    <div class="contact-info">
      <h1>Contact Us</h1>
      <p>Have questions or feedback about Thrifto? We'd love to hear from you! Fill out the form or reach out to us directly.</p>

      <div class="contact-methods">
        <div class="contact-method">
          <div class="icon">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
              <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
              <polyline points="22,6 12,13 2,6"></polyline>
            </svg>
          </div>
          <div class="contact-details">
            <h3>Email</h3>
            <p><a href="mailto:support@thrifto.com">support@thrifto.com</a></p>
          </div>
        </div>

        <div class="contact-method">
          <div class="icon">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
              <path d="M22 16.92v3a2 2 0 0 1-2.18 2 19.79 19.79 0 0 1-8.63-3.07 19.5 19.5 0 0 1-6-6 19.79 19.79 0 0 1-3.07-8.67A2 2 0 0 1 4.11 2h3a2 2 0 0 1 2 1.72 12.84 12.84 0 0 0 .7 2.81 2 2 0 0 1-.45 2.11L8.09 9.91a16 16 0 0 0 6 6l1.27-1.27a2 2 0 0 1 2.11-.45 12.84 12.84 0 0 0 2.81.7A2 2 0 0 1 22 16.92z"></path>
            </svg>
          </div>
          <div class="contact-details">
            <h3>Phone</h3>
            <p><a href="tel:+18005551234">1-800-555-1234</a></p>
            <p class="hours">Available Mon-Fri, 9am-5pm EST</p>
          </div>
        </div>

        <div class="contact-method">
          <div class="icon">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
              <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"></path>
              <circle cx="12" cy="10" r="3"></circle>
            </svg>
          </div>
          <div class="contact-details">
            <h3>Office</h3>
            <p>123 Fashion Street<br>New York, NY 10001</p>
          </div>
        </div>
      </div>
    </div>

    <div class="contact-form-container">
      <div *ngIf="isSubmitted" class="form-success">
        <svg xmlns="http://www.w3.org/2000/svg" width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round">
          <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
          <polyline points="22 4 12 14.01 9 11.01"></polyline>
        </svg>
        <h2>Thank You!</h2>
        <p>Your message has been sent successfully. We'll get back to you as soon as possible.</p>
        <button class="btn btn-primary" (click)="isSubmitted = false">Send Another Message</button>
      </div>

      <form *ngIf="!isSubmitted" [formGroup]="contactForm" (ngSubmit)="onSubmit()">
        <div class="form-group">
          <label for="name">Your Name</label>
          <input type="text" id="name" formControlName="name" class="form-control">
          <div *ngIf="nameControl?.invalid && nameControl?.touched" class="error-message">
            Please enter your name.
          </div>
        </div>

        <div class="form-group">
          <label for="email">Email Address</label>
          <input type="email" id="email" formControlName="email" class="form-control">
          <div *ngIf="emailControl?.invalid && emailControl?.touched" class="error-message">
            Please enter a valid email address.
          </div>
        </div>

        <div class="form-group">
          <label for="subject">Subject</label>
          <input type="text" id="subject" formControlName="subject" class="form-control">
          <div *ngIf="subjectControl?.invalid && subjectControl?.touched" class="error-message">
            Please enter a subject.
          </div>
        </div>

        <div class="form-group">
          <label for="message">Message</label>
          <textarea id="message" formControlName="message" class="form-control" rows="5"></textarea>
          <div *ngIf="messageControl?.invalid && messageControl?.touched" class="error-message">
            Please enter a message (at least 10 characters).
          </div>
        </div>

        <button type="submit" class="btn btn-primary" [disabled]="contactForm.invalid || isSubmitting">
          {{ isSubmitting ? 'Sending...' : 'Send Message' }}
        </button>
      </form>
    </div>
  </div>
</div>
