// src/app/components/account-settings/account-settings.component.ts
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../../services/auth.service';
import { NotificationService } from '../../services/notification.service';
import { TwoFactorSetupDto } from '../../models/two-factor.model';
import { User } from '../../models/user.model';

@Component({
    selector: 'app-account-settings',
    templateUrl: './account-settings.component.html',
    styleUrls: ['./account-settings.component.scss']
})
export class AccountSettingsComponent implements OnInit {
    currentUser: User | null = null;
    twoFactorEnabled = false;
    isLoading = true;
    isSubmitting = false;

    // 2FA Setup
    setupForm: FormGroup;
    setupData: TwoFactorSetupDto | null = null;
    showSetupForm = false;
    recoveryCodes: string[] = [];
    showRecoveryCodes = false;

    constructor(
        private authService: AuthService,
        private notificationService: NotificationService,
        private formBuilder: FormBuilder
    ) {
        this.setupForm = this.formBuilder.group({
            code: ['', [Validators.required, Validators.pattern(/^\d{6}$/)]]
        });
    }

    ngOnInit(): void {
        this.currentUser = this.authService.getCurrentUser();
        this.loadTwoFactorStatus();
    }

    get f() { return this.setupForm.controls; }

    loadTwoFactorStatus(): void {
        this.authService.get2FAStatus().subscribe({
            next: (response) => {
                this.twoFactorEnabled = response.twoFactorEnabled;
                this.isLoading = false;
            },
            error: (error) => {
                this.notificationService.show('Failed to load 2FA status', 'error');
                this.isLoading = false;
            }
        });
    }

    startTwoFactorSetup(): void {
        this.isSubmitting = true;
        this.authService.setup2FA().subscribe({
            next: (response) => {
                this.setupData = response;
                this.showSetupForm = true;
                this.isSubmitting = false;
            },
            error: (error) => {
                this.notificationService.show('Failed to generate QR code', 'error');
                this.isSubmitting = false;
            }
        });
    }

    enableTwoFactor(): void {
        if (this.setupForm.invalid) {
            return;
        }

        this.isSubmitting = true;
        this.authService.enable2FA(this.f['code'].value).subscribe({
            next: (response) => {
                this.twoFactorEnabled = true;
                this.showSetupForm = false;
                this.recoveryCodes = response.recoveryCodes || [];
                this.showRecoveryCodes = true;
                this.setupForm.reset();
                this.isSubmitting = false;
                this.notificationService.show('Two-factor authentication enabled successfully', 'success');
            },
            error: (error) => {
                this.isSubmitting = false;
                this.notificationService.show(error.error?.message || 'Invalid verification code', 'error');
            }
        });
    }

    disableTwoFactor(): void {
        if (!confirm('Are you sure you want to disable two-factor authentication? This will make your account less secure.')) {
            return;
        }

        this.isSubmitting = true;
        this.authService.disable2FA().subscribe({
            next: () => {
                this.twoFactorEnabled = false;
                this.isSubmitting = false;
                this.notificationService.show('Two-factor authentication disabled', 'success');
            },
            error: (error) => {
                this.isSubmitting = false;
                this.notificationService.show('Failed to disable 2FA', 'error');
            }
        });
    }

    cancelSetup(): void {
        this.showSetupForm = false;
        this.setupData = null;
        this.setupForm.reset();
    }

    downloadRecoveryCodes(): void {
        const content = this.recoveryCodes.join('\n');
        const blob = new Blob([content], { type: 'text/plain' });
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = 'thrifto-recovery-codes.txt';
        link.click();
        window.URL.revokeObjectURL(url);
    }

    copyRecoveryCodes(): void {
        const content = this.recoveryCodes.join('\n');
        navigator.clipboard.writeText(content).then(() => {
            this.notificationService.show('Recovery codes copied to clipboard', 'success');
        });
    }

    acknowledgeRecoveryCodes(): void {
        this.showRecoveryCodes = false;
        this.recoveryCodes = [];
    }
}
