// src/app/components/shared/notification/notification.component.ts
import { Component, OnInit } from '@angular/core';
import { NotificationService, Notification } from '../../../services/notification.service';
import { trigger, transition, style, animate } from '@angular/animations';

@Component({
  selector: 'app-notification',
  templateUrl: './notification.component.html',
  styleUrls: ['./notification.component.scss'],
  animations: [
    trigger('fadeInOut', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(-10px)' }),
        animate('300ms ease-out', style({ opacity: 1, transform: 'translateY(0)' }))
      ]),
      transition(':leave', [
        animate('300ms ease-in', style({ opacity: 0, transform: 'translateY(-10px)' }))
      ])
    ])
  ]
})

export class NotificationComponent implements OnInit {
  notifications: Notification[] = [];

  constructor(private notificationService: NotificationService) { }

  ngOnInit(): void {
    this.notificationService.notifications$.subscribe(notifications => {
      this.notifications = notifications;
    });
  }

  close(id: string): void {
    this.notificationService.close(id);
  }
}
<!-- src/app/components/shared/notification/notification.component.html -->
<div class="notifications-container">
  <div *ngFor="let notification of notifications"
       class="notification"
       [ngClass]="notification.type"
       [@fadeInOut]>
    <div class="notification-content">
      {{ notification.message }}
    </div>
    <button class="close-btn" (click)="close(notification.id)">&times;</button>
  </div>
</div>
