    });
    return columns;
  }
  getMagazineItemHeight(columnIndex, itemIndex) {
    // Create varied heights for Pinterest-style layout
    const heights = ['240px', '320px', '280px', '360px', '200px', '300px'];
    const heightIndex = (columnIndex * 10 + itemIndex) % heights.length;
