    const params = {
      page: this.paginationService.currentPage,
      pageSize: this.paginationService.currentPageSize
    };
    if (this.selectedQuality) params.quality = this.selectedQuality;
    if (this.sortBy !== 'newest') params.sortBy = this.sortBy;
    // ✅ FIXED: Always include price range if it's not the full range
    if (this.priceRange.min > 0 || this.priceRange.max < 10000) {
      params.minPrice = this.priceRange.min;
