export class HomeComponent {
  constructor(listingService, router) {
    this.listingService = listingService;
    this.router = router;
    this.destroy$ = new Subject();
    this.featuredListings = [];
    this.recentListings = [];
    this.heroFeaturedItem = null;
    this.isLoading = true;
    this.activeQuality = '';
    // ✅ ADD: Search-related properties for the search-bar component
    this.searchQuery = '';
    this.popularSearches = ['Vintage Denim', 'Designer Dresses', 'Leather Jackets', 'Summer Tops', 'Sneakers', 'Handbags', 'Coats', 'Accessories', 'Sustainable Fashion', 'Pre-loved Luxury'];
    // Dynamic search placeholders
    this.searchPlaceholders = ['Vintage denim jacket...', 'Designer handbag...', 'Cozy sweater...', 'Statement jewelry...', 'Summer dress...'];
    this.currentPlaceholderIndex = 0;
    // Impact metrics for storytelling
    this.impactMetrics = [{
      icon: 'fas fa-recycle',
      value: '12.5K',
      label: 'Items Rescued'
    }, {
      icon: 'fas fa-leaf',
      value: '89%',
      label: 'Waste Reduced'
    }, {
      icon: 'fas fa-users',
      value: '3.2K',
      label: 'Happy Buyers'
    }];
    this.qualities = [{
      label: 'New',
      value: 'NEW'
    }, {
      label: 'Like New',
      value: 'LIKE NEW'
    }, {
      label: 'Barely Worn',
      value: 'BARELY WORN'
    }, {
      label: 'Used',
      value: 'USED'
    }];
  }
  ngOnInit() {
    this.loadListings();
    this.startSearchPlaceholderRotation();
  }
  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
  }
  loadListings() {
    // Load featured listings
    this.listingService.getListings({
      pageSize: 20
    }).subscribe({
      next: response => {
        if (response.items && response.items.length > 0) {
          const shuffled = [...response.items];
          for (let i = shuffled.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
          }
          this.featuredListings = shuffled.slice(0, 6);
          this.heroFeaturedItem = shuffled[0];
        }
        this.isLoading = false;
      },
      error: error => {
        console.error('Error loading featured listings', error);
        this.isLoading = false;
      }
    });
    // Load recent listings
    this.listingService.getNewListings(8).subscribe({
      next: listings => {
        this.recentListings = listings;
      },
      error: error => {
        console.error('Error loading recent listings', error);
      }
    });
  }
  startSearchPlaceholderRotation() {
    interval(3000).pipe(takeUntil(this.destroy$)).subscribe(() => {
      this.currentPlaceholderIndex = (this.currentPlaceholderIndex + 1) % this.searchPlaceholders.length;
    });
  }
  // ✅ ADD: Search-related methods for the search-bar component
  onSearchUpdate(query) {
    this.searchQuery = query;
    // Optional: You could add live search preview functionality here
    // For example, showing search suggestions or live results
    console.log('Live search update:', query);
  }
  onSuggestionSelected(suggestion) {
    this.searchQuery = suggestion;
    // Navigate to search page with the selected suggestion
    this.router.navigate(['/search'], {
      queryParams: {
        q: suggestion
      }
    });
  }
  // Helper methods for the template
  getSearchPlaceholder() {
    return this.searchPlaceholders[this.currentPlaceholderIndex];
  }
  getTotalItemsSaved() {
    return 12500 + this.featuredListings.length + this.recentListings.length;
  }
  getTotalListingsCount() {
    return 1250; // This could come from your API
